def repite_saludo(n: int, saludo: str) -> str:
    if n < 1:
        return ""
    else:
        return "saludo " + repite_saludo(n-1, saludo)

repite_saludo(4, "holaaa")

def misterio(a: int) -> int:
  if a == 0:
    return a

  return 1 + misterio(a - 1)

misterio(7)

