def f(n: int, d: int) -> None:
    """
    Calcula los factores primos de n.
    """
    while n > 1:
        if n % d == 0:
            print(d)
            n = n//d
        else:
            d += 1

f(30, 2)

def mystery ( a: int, b: int ) -> int:
  if ( b == 0) : return a
  return mystery (2 * a , b -1)

result = mystery (7 , 3)
print (result)

def naturales(n: int) -> int:
    if n == 0:
        return n
    else:
        print(n)
        naturales(n-1)

naturales(6)

def recursiva(t: int, k: int) -> int:
    if t >= 100:
        return k
    else:
        return recursiva(t+k, k+1)


def iterativa(t: int, k: int) -> int:
    while t < 100:
        t = t + k
        k = k + 1
    return k

t = 190
k = 50
print(recursiva(t, k))
print(iterativa(t, k))

def triangular(t: int) -> None:
    if t == 0:
        return t
    else:
        return t + triangular (t-1)

triangular(6)

def digitos(n: int) -> int:

    if n < 10:
        return 1
    else:

        return 1 + digitos(n // 10)


digitos(18990000)

def espotencia(n: int, b:int) -> bool:
    if n % b != n:
        return False
    else:
        espotencia(n // b, b)
        return True


espotencia(70,10)

def max(ls: list[float]) -> float:
    if ls == []:
        return float('-inf')
    else:
        first = ls[0]
        tail = ls[1]
        max_tail = max(tail)
        if first > max_tail:
            return first
        else:
            return max_tail

ls = [1,5,8,9]
resultado = max(ls)
print(resultado)

def iterativa(l: list[int]) -> int:
  c = 1
  for i in l:
    c = c * i
  return c

def replicar(lista, n):
    if n == 1:
        return lista
    else:
        return lista + replicar(lista, n - 1)

replicar([1, 3, 3, 7], 2)
